sys_script_include.script:
var testDates = Class.create();
testDates.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	myFunc: function() {

			var stringStart = "22/10/2018";
			var stringEnd = "25/10/2018";
			var gdt1 = new GlideDateTime(stringStart);
			var gdt2 = new GlideDateTime(stringEnd);
			var dateStart = gdt1.getNumericValue() / 1000;
			var dateEnd = gdt2.getNumericValue() / 1000;
			gs.addInfoMessage('start_date ' + dateStart + ' '  + dateEnd );

	},
    type: 'testDates'

});
